var http = require("http");

http.createServer(function (req, res) { 
  console.log("C"); 
  var count = 5;
  var timer = setInterval(function () {
    console.log("-");
    if (count == 0) {
      clearInterval(timer);
      console.log('.');
      return res.end("-OK-");
    } 
    count--;
    res.write("");
  }, 1500);
}).listen(3000);
